function init()
{

	host.getMidiInPort(0).setMidiCallback(onMidi);
	host.getMidiInPort(0).setSysexCallback(onSysex);

	/* TRANSPORT */

	transport = host.createTransportSection();
	arranger = host.createArrangerSection(0);

	transport.addAutomationOverrideObserver(function(on)
	{
		sendNoteOn(0, NOTE.FBUTTON1, on ? 127 : 0);
	});
	arranger.addPlaybackFollowObserver(function(on)
	{
		sendNoteOn(0, NOTE.FBUTTON3, on ? 127 : 0);
		isPlaybackFollow = on;
	});

	// transport.addIsPlayingObserver(function(on)
	// {
	// // setButton(play,on);
	// });
	// transport.addIsLoopActiveObserver(function(on)
	// {
	// // setButton(loop,on);
	// });
	// transport.addIsRecordingObserver(function(on)
	// {
	// // setButton(record,on);
	// });
	transport.addIsWritingArrangerAutomationObserver(function(on)
	{
		// sendNoteOn(0, 61, on ? 127 : 0);
	});

	/* APPLICATION */

	application = host.createApplicationSection();

	/* TRACK BANK */

	trackBank = host.createTrackBankSection(8, 3, 0);

	for ( var t = 0; t < 8; t++)
	{
		var track = trackBank.getTrack(t);

		track.addVuMeterObserver(15, -1, true, function()
		{
		});

		track.getVolume().addValueObserver(128, makeIndexedFunction(t, function(index, value)
		{
			modePages[MODE_PAGE.MIXER].setEncoder(0, index, value);
		}));
		track.getVolume().setLabel("V" + (t + 1));
		track.getPan().addValueObserver(128, makeIndexedFunction(t, function(index, value)
		{
			modePages[MODE_PAGE.MIXER].setEncoder(1, index, value);
		}));
		track.getPan().setLabel("P" + (t + 1));
		track.getSend(0).addValueObserver(128, makeIndexedFunction(t, function(index, value)
		{
			// modePages[MODE_PAGE.MIXER].setEncoder(2, index, value);
			modePages[MODE_PAGE.SEND].setEncoder(2, index, value);
		}));
		track.getSend(1).addValueObserver(128, makeIndexedFunction(t, function(index, value)
		{
			modePages[MODE_PAGE.SEND].setEncoder(1, index, value);
		}));
		track.getSend(2).addValueObserver(128, makeIndexedFunction(t, function(index, value)
		{
			modePages[MODE_PAGE.SEND].setEncoder(0, index, value);
		}));

		track.addIsSelectedObserver(makeIndexedFunction(t, function(col, isSelected)
		{
			// sendNoteOn(0, NOTE.BUTTONROW_LOWER0 + col, isSelected ? 127 :
			// 0);
			buttonPages[BUTTON_PAGE.SELECT_SOLO].setButton(lowerRow, col, isSelected ? 127 : 0);
		}));
		track.getMute().addValueObserver(makeIndexedFunction(t, function(col, on)
		{
			buttonPages[BUTTON_PAGE.ARM_MUTE].setButton(upperRow, col, on ? 127 : 0);
		}));
		track.getSolo().addValueObserver(makeIndexedFunction(t, function(col, on)
		{
			buttonPages[BUTTON_PAGE.SELECT_SOLO].setButton(upperRow, col, on ? 127 : 0);
		}));
		track.getArm().addValueObserver(makeIndexedFunction(t, function(col, on)
		{
			// sendNoteOn(0, NOTE.BUTTONROW_UPPER0 + col, on ? 127 : 0);
			buttonPages[BUTTON_PAGE.ARM_MUTE].setButton(lowerRow, col, on ? 127 : 0);
		}));
	}
	//
	// /* CURSOR TRACK */
	//
	cursorTrack = host.createCursorTrackSection(2, 0);

	/* CURSOR DEVICE */

	cursorDevice = host.createCursorDeviceSection(8);
	cursorDevice.addSelectedPageObserver(0, function(page)
	{
	});
	primaryInstrument = cursorTrack.getPrimaryInstrument();
	for ( var p = 0; p < 8; p++)
	{
		var macro = primaryInstrument.getMacro(p).getAmount();
		macro.addValueObserver(128, makeIndexedFunction(p, function(col, value)
		{
			modePages[MODE_PAGE.SEND].setEncoder(3, col, value);
			modePages[MODE_PAGE.MIXER].setEncoder(3, col, value);
			modePages[MODE_PAGE.DEVICE].setEncoder(3, col, value);
		}));
		// macro.addIsMappingObserver(getObserverIndexFunc(p, isMapping)); //TODO
	}
	for ( var p = 0; p < 8; p++)
	{
		var parameter = cursorDevice.getParameter(p);
		parameter.addValueObserver(128, makeIndexedFunction(p, function(col, value)
		{
			// modePages[MODE_PAGE.SEND].setEncoder(2, col, value);
			modePages[MODE_PAGE.MIXER].setEncoder(2, col, value);
			modePages[MODE_PAGE.DEVICE].setEncoder(2, col, value);
		}));
		parameter.setLabel("P" + (p + 1));
	}
	for ( var p = 0; p < 8; p++)
	{
		var common = cursorDevice.getCommonParameter(p);
		common.addValueObserver(128, makeIndexedFunction(p, function(col, value)
		{
			modePages[MODE_PAGE.DEVICE].setEncoder(0, col, value);
		}));
		common.setLabel("common" + (p + 1));
	}
	for ( var p = 0; p < 8; p++)
	{
		var env = cursorDevice.getEnvelopeParameter(p);
		env.addValueObserver(128, makeIndexedFunction(p, function(col, value)
		{
			modePages[MODE_PAGE.DEVICE].setEncoder(1, col, value);
		}));
		env.setLabel("env" + (p + 1));
	}

	// ///// for step sequencer
	// cursorClip = host.createCursorClipSection(16, 1);
	// cursorClip.addStepDataObserver(seqPage.onStepExists);
	// cursorClip.addPlayingStepObserver(seqPage.onStepPlay);
	// ///////////////

	// sendSysex(BCRBitwigPreset);
	initState();
}

/* ----------------------- onMidi ----------------- */

function onMidi(status, data1, data2)
{
	// printMidi(status, data1, data2);

	/* ---------------------- Buttons --- */
	if (isNoteOn(status))
	{

		var button = data1;
		var pressed = data2;

		if (button == NOTE.EGBUTTON1)
		{
			isAlt = pressed > 0;
			if (activeModePage != MODE_PAGE.DEVICE)
			{
				isAlt ? setButtonPage(BUTTON_PAGE.ARM_MUTE) : setButtonPage(BUTTON_PAGE.SELECT_SOLO);
			}
		}
		else if (button == NOTE.EGBUTTON3)
		{
			isShift = pressed > 0;
		}
		// else if (button >= NOTE.CLICKENCBUTTON0 && button < (NOTE.CLICKENCBUTTON0 + 8))
		// {
		// isEncoderClick[button - NOTE.CLICKENCBUTTON0] = pressed > 0;
		// }

		if (pressed > 0) // Check button presses (but not releases) in here
		{
			switch (button)
			{
				case NOTE.EGBUTTON2:
					switchMode();
					break;
				case NOTE.EGBUTTON4:
					break;
				case NOTE.FBUTTON1:
					transport.resetAutomationOverrides();
					break;
				case NOTE.FBUTTON2:
					transport.returnToArrangerment();
					break;
				case NOTE.FBUTTON3:
					arranger.togglePlaybackFollow();
					break;
				// case NOTE.FBUTTON4:
				// application.toggleAutomationEditor();
				// break;
				case NOTE.PREV_PRESET:
					isShift ? cursorDevice.switchToPreviousPresetCategory() : cursorDevice.switchToPreviousPreset();
					break;
				case NOTE.NEXT_PRESET:
					isShift ? cursorDevice.switchToNextPresetCategory() : cursorDevice.switchToNextPreset();
					break;
				// case NOTE.UBUTTON1:
				// cursorDevice.selectPrevious();
				// break;
				// case NOTE.UBUTTON2:
				// cursorDevice.selectNext();
				// break;
				// case NOTE.UBUTTON3:
				// cursorDevice.previousParameterPage();
				// break;
				// case NOTE.UBUTTON4:
				// cursorDevice.nextParameterPage();
				// break;
				case NOTE.UBUTTON1:
					isShift ? trackBank.scrollTracksPageUp() : cursorDevice.selectPrevious();
					break;
				case NOTE.UBUTTON2:
					isShift ? trackBank.scrollTracksPageDown() : cursorDevice.selectNext();
					break;
				case NOTE.UBUTTON3:
					isShift ? application.toggleAutomationEditor() : application.toggleNoteEditor();
					break;
				case NOTE.UBUTTON4:
					isShift ? application.toggleMixer() : application.toggleDevices();
					break;
			}

			if (button >= NOTE.BUTTONROW_UPPER0 && button < NOTE.BUTTONROW_UPPER0 + 8)
			{
				var track = button - NOTE.BUTTONROW_UPPER0;

				switch (activeModePage)
				{
					case MODE_PAGE.SEND:
						if (isAlt)
							trackBank.getTrack(track).getMute().toggle();
						else trackBank.getTrack(track).getSolo().toggle();
						break;
					case MODE_PAGE.DEVICE:
						cursorDevice.getModulationSource(track).toggleIsMapping();
						return;
						break;
					case MODE_PAGE.MIXER:
						if (isAlt)
							trackBank.getTrack(track).getMute().toggle();
						else trackBank.getTrack(track).getSolo().toggle();
						break;
				}
			}
			else if (button >= NOTE.BUTTONROW_LOWER0 && button < NOTE.BUTTONROW_LOWER0 + 8)
			{
				var track = button - NOTE.BUTTONROW_LOWER0;
				switch (activeModePage)
				{
					case MODE_PAGE.SEND:
						if (isAlt)
							trackBank.getTrack(track).getArm().toggle();
						else trackBank.getTrack(track).select();
						break;
					case MODE_PAGE.DEVICE:
						if (isAlt)
							trackBank.getTrack(track).getArm().toggle();
						else trackBank.getTrack(track).select();
						return;
						break;
					case MODE_PAGE.MIXER:
						if (isAlt)
							trackBank.getTrack(track).getArm().toggle();
						else trackBank.getTrack(track).select();
						break;
				}
			}
			else if (button >= NOTE.CLICKENCBUTTON0 && button < (NOTE.CLICKENCBUTTON0 + 8))
			{
				var eClick = button - NOTE.CLICKENCBUTTON0;
				primaryInstrument.getMacro(eClick).getModulationSource().toggleIsMapping();
			}
		}
		else if (pressed == 0) // Check button releases. important with BCR and
		// BCF because LED state must be set on release
		{
			if (button == NOTE.EGBUTTON2)
			{
				switch (activeModePage)
				{
					case MODE_PAGE.MIXER:
						sendNoteOn(0, NOTE.EGBUTTON2, 0);
						break;
					case MODE_PAGE.SEND:
						sendNoteOn(0, NOTE.EGBUTTON2, 127);
						break;
					case MODE_PAGE.DEVICE:
						sendNoteOn(0, NOTE.EGBUTTON2, 0);
						break;
				}
			}
			if (button == NOTE.FBUTTON3)
			{
				sendNoteOn(0, NOTE.FBUTTON3, isPlaybackFollow ? 127 : 0);
			}
			if (activeModePage == MODE_PAGE.DEVICE)
			{
				for ( var i = 0; i < 8; i++)
				{
					isAlt ?buttonPages[BUTTON_PAGE.ARM_MUTE].sendButtonLED(0, i):buttonPages[BUTTON_PAGE.SELECT_SOLO].sendButtonLED(0, i);
				}
			}
			else if (activeModePage != MODE_PAGE.DEVICE) isAlt ? setButtonPage(BUTTON_PAGE.ARM_MUTE) : setButtonPage(BUTTON_PAGE.SELECT_SOLO);

		}
	}

	/* encoders */
	if (isChannelController(status))
	{
		var cc = data1;
		var delta = data2;
		// var relativeRange = [cc - CC.CLICKENC0] || isShift ? 512 : 128;
		var relativeRange = isShift ? 512 : 128;
		if (delta > 64)
		{
			delta = (-1) * (128 - delta);
		}
		if (cc >= CC.CLICKENC0 && cc < (CC.CLICKENC0 + 8))
		{
			var col = cc - CC.CLICKENC0;

			getEncoderObjectPath(3, col).inc(delta, relativeRange);
		}
		if (cc >= CC.ENCTOP0 && cc < (CC.ENCTOP0 + 8))
		{
			var col = cc - CC.ENCTOP0;

			getEncoderObjectPath(2, col).inc(delta, relativeRange);
		}
		if (cc >= CC.ENCMID0 && cc < (CC.ENCMID0 + 8))
		{
			var col = cc - CC.ENCMID0;

			getEncoderObjectPath(1, col).inc(delta, relativeRange);
		}
		if (cc >= CC.ENCLOW0 && cc < (CC.ENCLOW0 + 8))
		{
			var col = cc - CC.ENCLOW0;

			getEncoderObjectPath(0, col).inc(delta, relativeRange);
		}

	}
}

function getEncoderObjectPath(row, index)
{
	if (activeModePage == MODE_PAGE.MIXER)// ///////////////// DEACTIVATED
	{
		switch (row)
		{
			case 0:
				return trackBank.getTrack(index).getVolume();
			case 1:
				return trackBank.getTrack(index).getPan();
				break;
			case 2:
				return cursorDevice.getParameter(index);
				break;
			case 3:
				return primaryInstrument.getMacro(index).getAmount();
				break;
		}
	}

	// /////////////////////////////////////////////////////////////////////////////
	else if (activeModePage == MODE_PAGE.DEVICE)
	{
		switch (row)
		{
			case 0:
				return cursorDevice.getCommonParameter(index);
				break;
			case 1:
				return cursorDevice.getEnvelopeParameter(index);
				break;
			case 2:
				return cursorDevice.getParameter(index);
				break;
			case 3:
				return primaryInstrument.getMacro(index).getAmount();
				break;
		}
	}
	// //////////////////////////////////////////////////////////////////////////////////////
	else if (activeModePage == MODE_PAGE.SEND)
	{
		switch (row)
		{
			case 0:
				return trackBank.getTrack(index).getSend(2);
				break;
			case 1:
				return trackBank.getTrack(index).getSend(1);
				break;
			case 2:
				return trackBank.getTrack(index).getSend(0);
				break;
			case 3:
				return primaryInstrument.getMacro(index).getAmount();
				// return cursorDevice.getParameter(index);
		}
	}
	else if (activeModePage == MODE_PAGE.DEVICE) return;
}
function switchMode()
{
	if (activeModePage == MODE_PAGE.DEVICE)
	{
		setModePage(MODE_PAGE.SEND);
		isAlt ? setButtonPage(BUTTON_PAGE.ARM_MUTE) : setButtonPage(BUTTON_PAGE.SELECT_SOLO);
		setIndications("send");
	}
	else if (activeModePage == MODE_PAGE.SEND)
	{
		setModePage(MODE_PAGE.DEVICE);
		isAlt ? setButtonPage(BUTTON_PAGE.ARM_MUTE) : setButtonPage(BUTTON_PAGE.SELECT_SOLO);
		setIndications("device");

		// setModePage(MODE_PAGE.DEVICE);
		// setButtonPage(BUTTON_PAGE.DEVICE);
	}
	// else if (activeModePage == MODE_PAGE.DEVICE)
	// {
	// setModePage(MODE_PAGE.MIXER);
	// isAlt ? setButtonPage(BUTTON_PAGE.ARM_MUTE) : setButtonPage(BUTTON_PAGE.SELECT_SOLO);
	// }
	else sendNoteOn(0, NOTE.EGBUTTON2, 0); // all enc_assign off
}

function makeIndexedFunction(index, f)
{
	return function(value)
	{
		f(index, value);
	};
}
